INSERT INTO `wines` (`color`, `sugar`, `strength`, `photo`, `name`, `article`, `real_name`, `year`, `country`, `region`, `manufacturer`, `grape`, `price`, `body`, `smell_description`, `taste_description`, `food_description`, `style`, `recommend`, `type`, `created_at`, `updated_at`, `deleted_at`)
VALUES
    ('Красное', 'сухое', '14.5%', 'https://images.winechef.com/media/images/x454/5c064369df40d/749bfa818a90d6674c4f0f9084b1d40b.jpeg', '\"CrossBarn\" by Paul Hobbs, Cabernet Sauvignon, Napa Valley', '1d5884a3ffa7', '\"КроссБарн\" Каберне Совиньон', '2019', 'США', 'Калифорния', 'Paul Hobbs', 'Каберне Совиньон, Сира, Мерло, Каберне Фран', 8667, 'Полнотелое', 'У вина соблазнительный, насыщенный аромат с преобладанием полутонов спелых слив, лесных ягод и выразительным шлейфом из пряных нюансов тимьяна, гвоздики, ванили и шалфея.', 'Вино поражает приятным фруктовым аккордом (нотки сливы, вишни) и нежной сливочной текстурой, на фоне которой выделяются мелкозернистые танины. Элегантное, долгое послевкусие с выразительными штрихами табака, лакрицы и графита.', 'Вино является идеальным партнером для шашлыка, жареного мяса и мяса-гриль. Рекомендуется к дичи и сырам.', 'Фруктовые и ароматные, Элегантные и бархатистые, Насыщенные и богатые', 'Курица, Сыр, Дичь', 'Вино', '2024-02-03 11:37:26', '2024-02-03 11:37:26', NULL),
    ('Красное', 'сухое', '13.5%', 'https://images.winechef.com/media/images/x454/5c08f4a646153/60c87ba075233762a5a025b5d675eb5b.jpeg', 'Domaine Follin-Arbelet, Aloxe-Corton AOC', '3ff13e94ab68', 'Домен Фоллин-Арбеле, Алокс-Кортон', '2013', 'Франция', 'Бургундия', 'Domaine Follin-Arbelet', 'Пино Нуар', 4967, 'Среднее', 'Вино с обильными ароматами сухих лепестков розы, фиалки, темных фруктов и ягод.', 'Вино обладает элегантным, сочным вкусом  с прекрасной структурой, гладкой текстурой, округлыми танинами, хрустящими, точными и чистыми нотами темных ягод, особенно черники.', 'Вино является отличным дополнением к дичи, жареному мясу и сырам.', '', '', 'Вино', '2024-02-03 11:37:26', '2024-02-03 11:37:26', NULL),
    ('Красное', 'сухое', '13.5%', '', 'Domaine Fourrier, Gevrey-Chambertin Vieille Vigne AOC', '3dc23c7b840a', 'Домен Фуррье, Жевре-Шамбертен Вьей Винь', '2020', 'Франция', 'Бургундия', 'Domaine Fourrier', 'Пино Нуар', 10400, 'Среднее', 'Вино покоряет многогранным, щедрым ароматом, в котором, подобно симфонии, сплелись ноты красной вишни, малины, лакрицы, подлеска и цукатов.', 'Вино демонстрирует полный, округлый вкус с эластичной текстурой, фруктово-бальзамическими тонами и мягкими танинами в стойком послевкусии.', '', 'Фруктовые и ароматные, Элегантные и бархатистые, Пряные и пикантные', 'Сыр, Дичь, Говядина, Паста', 'Вино', '2024-02-03 11:37:26', '2024-02-03 11:37:26', NULL),
    ('Красное', 'сухое', '13%', '', 'Benedicte & Stephane Tissot, \"DD\", Arbois AOC', 'af3f2ca0d883', 'Бенедикт & Стефан Тиссо, \"ДД\"', '', 'Франция', 'Бургундия', 'Domaine Andre et Mireille Tissot', 'Пино Нуар, Пульсар', 2800, 'Полнотелое', 'В выразительном аромате вина ноты спелой вишни и красных ягод украшены нотками апельсиновой цедры и цветов.', 'Округлый, сочный, бархатистый вкус вина освежает гармоничной кислотностью, подчеркивающей тона красных фруктов и ягод, которые сменяются в приятном послевкусии легкими цветочными и грибными оттенками.', 'Вино хорошо сочетается с жареным цыпленком, пастой, колбасами, сырами. Идеально для пикника.', 'Фруктовые и ароматные, Элегантные и бархатистые, Пряные и пикантные', 'Сыр, Дичь, Говядина, Паста', 'Вино', '2024-02-03 11:37:26', '2024-02-03 11:37:26', NULL),
    ('Красное', 'сухое', '12.5%', '', 'Elian Da Ros, \"Le Vin est Une Fete\", Cotes du Marmandais AOC', '5b4c445145b2', 'Элиан Да Рос, \"Ле Ван эт Юн Фэт\"', '', 'Франция', 'Юго-запад Франции', 'Elian Da Ros', 'Мерло, Каберне Фран', 1867, 'Среднее', 'В аромате вина ощущаются ноты сочных красных фруктов, лесных ягод, белого перца и земли.', 'Вкус вина свежий, хрустящий, округлый, питкий, фруктовый, с тонкой танинной структурой.', 'Вино приятно в качестве аперитива, но также легко сочетается с более щедрыми блюдами.', 'Фруктовые и ароматные, Элегантные и бархатистые, Пряные и пикантные', 'Сыр, Аперитив', 'Вино', '2024-02-03 11:37:26', '2024-02-03 11:37:26', NULL),
    ('Красное', 'сухое', '13.5%', 'https://images.winechef.com/media/images/x454/5c09c539a34a5/c6955924692dd9105586c32c52d9fc91.jpeg', 'Domaine Bertagna, Corton Grand Cru \"Les Grandes Lolieres\" AOC', 'b43a97fa27bc', 'Домен Бертанья, Кортон Гран Крю \"Ле Гранд Лольер\"', '2016', 'Франция', 'Бургундия', 'Domaine Bertagna', 'Пино Нуар', 10933, 'Полнотелое', 'Изысканный аромат вина наполнен оттенками черешни и малины и дополнен тонами сливового джема.', 'Вино обладает полнотелым вкусом с хорошей структурой и концентрацией. Послевкусие продолжительное, наполнено благородными танинами.', 'Вино идеально подходит к блюдам из белого мяса, а также составит классическую партию сырам.', 'Фруктовые и ароматные, Элегантные и бархатистые', 'Сыр, Дичь, Говядина', 'Вино', '2024-02-03 11:37:26', '2024-02-03 11:37:26', NULL),
    ('Красное', 'сухое', '13.5%', '', 'Domaine de Mouscaillo, Pinot Noir, Haute Vallee de l\'Aude IGP', '781c8738627b', 'Домен де Мускайо, Пино Нуар', '', 'Франция', 'Лангедок-Руссийон', 'Domaine de Mouscaillo', 'Пино Нуар', 4200, 'Среднее', 'Вино обладает ароматом умеренной силы, который раскрывается нотами вишни, клубники и специй.', 'В свежем вкусе вина хорошо выражена минеральная оставляющая. Щедрый фруктовый букет дополнен приятной кислотностью и ощутимыми танинами.', 'Вино составит сочетание с дичью и красным мясом.', 'Фруктовые и ароматные, Элегантные и бархатистые, Пряные и пикантные', 'Сыр, Дичь, Говядина', 'Вино', '2024-02-03 11:37:26', '2024-02-03 11:37:26', NULL),
	('Красное', 'сухое', '12.5%', '', 'Lignier Michelot Bourgogne Rouge', '7e8ebe484d55', 'Линье Мишело, Бургонь Пино Нуар', '', 'Франция', 'Бургундия', 'Domaine Michelot', 'Пино Нуар', 3267, 'Среднее', 'Приятный, элегантный аромат вина раскрывается оттенками вишни, черники, ежевики, сладких специй, пряностей и черного перца.', 'Вкус вина округлый, гармоничный, с бархатистой текстурой, фруктовой кислинкой и мелкозернистыми танинами и долгом послевкусии.', 'Вино хорошо сочетается с жареным мясом, мясом-гриль, мягкими сырами. Например, ягненок, тушеный с пряностями, овощной пастуший пирог, карпаччо из говядины с лепестками пармезана.', 'Фруктовые и ароматные, Элегантные и бархатистые, Пряные и пикантные', 'Сыр, Говядина, Паста', 'Вино', '2024-02-03 11:37:26', '2024-02-03 11:37:26', NULL),
	('Красное', 'сухое', '13%', 'https://images.winechef.com/media/images/x454/5c7925b77f90f/02f65e6615d9c746eff8d734e32a3aea.jpeg', 'Pavillon Rouge Du Chateau Margaux AOC', '7a0abb192915', 'Павийон дю Шато Марго', '2018', 'Франция', 'Бордо', 'Chateau Margaux', 'Каберне Совиньон, Пти Вердо, Мерло, Каберне Фран', 20533, 'Полнотелое', 'Потрясающий аромат вина наполнен тонами черных ягод (с доминирующими оттенками шелковицы и смородины) и щебня, нюансами ванили и кофе.', 'Классический, свежий, большой чистоты вкус вина раскрывается нотами красных и черных плодов, оттенками солодки, листьев табака, цветов, нюансами специй. Танины — тончайшие, сладкие, мягкие, округлые.', 'Вино рекомендуется подавать к изысканным мясным блюдам и легким закускам, дичи, нежной телятине, ягненку и сырному ассорти.', 'Фруктовые и ароматные, Элегантные и бархатистые, Пряные и пикантные', 'Сыр, Дичь, Говядина, Закуски, Телятина', 'Вино', '2024-02-03 11:37:26', '2024-02-03 11:37:26', NULL),
	('Красное', 'сухое', '12.5%', '', 'Francois Feuillet, Bourgogne AOC Pinot Noir', '1705e361244f', 'Франсуа Фейе, Бургонь Пино Нуар', '', 'Франция', 'Бургундия', 'David Duband', 'Пино Нуар', 2933, 'Среднее', 'Аромат вина наполнен оттенками фиалки, розы, малины, вишня, чай и легкими нюансами корицы.', 'Вкус вина зрелый, вязкий, округлый, с мягкой кислотностью, шелковистыми танинами, глубокими фруктовыми оттенками. В послевкусии остаются легкая кислинка и нюансы фруктового чая.', 'Вино хорошо сочетается с жареным мясом, пастой, горячими овощами и слегка пряными сырами.', 'Фруктовые и ароматные, Элегантные и бархатистые, Землистые и деликатные', 'Сыр, Говядина, Паста', 'Вино', '2024-02-03 11:37:26', '2024-02-03 11:37:26', NULL),
	('Красное', 'сухое', '13%', 'https://images.winechef.com/media/images/x454/5db8b00145fbd/8bdc1004beb7cc1be7a00dff9aeaaffe.png', 'Francois Feuillet Vosne-Romanée', '019078a22898', 'Вон-Романе', '2016', 'Франция', 'Бургундия', 'Francois Feuillet', '', 6600, 'Среднее', 'Тонкий цветочный аромат вина раскрывается выразительными оттенками фиалки на фоне целой симфонии тонов вишни, лакрицы, сливы и восточных специй.', 'Невероятно долгий, объемный вкус вина отличается идеальной структурой и балансом, маслянистой текстурой с элегантными танинами, плавно трансформируя силу в утонченность.', 'Вино прекрасно сочетается с традиционными для красных вин мясными блюдами и сырами. Идеальным сочетанием являются трюфели, мясо кролика и пернатой дичи, а из сыров лучше всего подойдут Сито, Сен-Нектер и Брийя-Саварен.', 'Ароматные и фруктовые, Элегантные и бархатистые, Пряные и пикантные', 'Сыр, Дичь, Говядина', 'Вино', '2024-02-03 11:37:26', '2024-02-03 11:37:26', NULL),
	('Красное', 'сухое', '14%', 'https://images.winechef.com/media/images/x454/5c792c57cf3d8/20883e820cfee504376b71de5fcbe22a.jpeg', 'Chateau Beaumont, Haut-Medoc AOC Cru Bourgeois Superieur', '1efbc14c92f7', 'Шато Бомон', '2019', 'Франция', 'Бордо', 'Chateau Beaumont', 'Каберне Совиньон, Пти Вердо, Мерло', 3067, 'Среднее', 'У вина цветочный, элегантный аромат, сотканный из нот вишни, земляники, гранита и специй.', 'Вкус вина свежий, хрустящий, с оттенками малины и ежевики, с плотной текстурой и долгим, соблазнительным послевкусием.', 'Вино будет отличным сопровождением для мясных блюд, запеченных овощей, лазаньи и, конечно же, сыров.', 'Фруктовые и ароматные, Элегантные и бархатистые', 'Сыр, Говядина, Мясные закуски', 'Вино', '2024-02-03 11:37:26', '2024-02-03 11:37:26', NULL),
	('Красное', 'сухое', '14%', 'https://images.winechef.com/media/images/x454/5da3312a29d81/7445bdb1bfce32bba7b22527de9c0f44.jpeg', 'Chateau Grand Rousseau', 'fa7a25757544', 'Шато Гран Руссо', '2017', 'Франция', 'Бордо', 'Pierre Montagnac', '', 3200, 'Среднее', 'Аромат вина стойкий, интенсивный, с нотами кислой черной вишни и элегантными древесными нюансами.', 'Вкус вина элегантный, гармоничный, сложный, с сильной структурой, состоящей из широкий и приятных танинов, нотами фруктов и пряностей и длительным послевкусием.', 'Особенно хорошо подойдет к ароматным и интенсивным блюдам, таким, как острые супы и макаронные блюда. Отлично сочетается с красным мясом и зрелыми сырами.', 'Ароматные и фруктовые, Элегантные и бархатистые, Пряные и пикантные', 'Сыр, Говядина, Паста', 'Вино', '2024-02-03 11:37:26', '2024-02-03 11:37:26', NULL),
	('Красное', 'сухое', '14.5%', 'https://images.winechef.com/media/images/x454/5c79365a68c93/9aa311c461ee6a56e25ea617214491cf.jpeg', 'Chateau Cos Labory, Saint Estephe, Grand Cru Classe', '41f5d348153e', 'Шато Кос Лабори', '2018', 'Франция', 'Бордо', 'Chateau Cos Labory', 'Каберне Совиньон, Мерло, Каберне Фран', 5467, 'Среднее', 'Глубокий аромат вина раскрывается акцентами цветов, спелых фруктов и терпкого дуба.', 'Вино обладает мощным, концентрированным вкусом с ярко выраженными фруктовыми тонами, легкими оттенками табака, кедра и специй. Освежающее, глубокое с хорошим потенциалом.', 'Вино хорошо подать к блюдам из мяса, говядине, баранине или дичи. Прекрасно сочетается с различными сырами.', '', 'Сыр, Дичь, Говядина', 'Вино', '2024-02-03 11:37:26', '2024-02-03 11:37:26', NULL),
	('Красное', 'сухое', '13.5%', 'https://images.winechef.com/media/images/x454/5c05aeaa933fe/7035ab94ae703e34f67ffd9f6a14df95.jpeg', 'Chateau Lalande-Borie, Saint-Julien AOC', '7230f2dba66d', 'Шато Лаланд-Бори', '2019', 'Франция', 'Бордо', 'Chateau Lalande Borie', 'Каберне Совиньон, Мерло, Каберне Фран', 4267, 'Среднее', 'В аромате вина преобладают оттенки черных ягод и вишни.', 'Вкус вина плотный, мощный, сложный, очень элегантный, прекрасно структурированный, с обильными тонами темных спелых ягод и фруктов (черники, черной вишни), оттенками орехов, шоколада, влажной земли, бархатистыми полными танинами и длительными послевкусием с травяными нюансами.', 'Вино прекрасно сочетается с жареным мясом, особенно мясом молодого барашка, а также с трюфелями, бефстроганов, ростбифами, пармской ветчиной и зрелыми сырами.', 'Фруктовые и ароматные, Элегантные и бархатистые, Пряные и пикантные', 'Сыр, Говядина, Мясные закуски, Грибы', 'Вино', '2024-02-03 11:37:26', '2024-02-03 11:37:26', NULL),
	('Красное', 'сухое', '13.5%', 'https://images.winechef.com/media/images/x454/5c05d636b42a0/e20e52fc57685991af78275b24ece290.jpeg', 'Chateau Lynch Bages, Pauillac AOC 5-eme Grand Cru Classe', 'd641bfe92a9b', 'Шато Линч Баж', '2019', 'Франция', 'Бордо', 'Chateau Lynch Bages', 'Каберне Совиньон, Пти Вердо, Мерло, Каберне Фран', 16467, 'Среднее', 'Превосходный аромат вина наполнен буйными тонами черных фруктов и ягод, оттенками красной сливы, цветов и сухой травы.', 'Вкус вина прочный, богатый, насыщенный, сочный и сфокусированный, с тонами темно-красных и фиолетовых фруктов, черных ягод, мяты, новой кожи, специй и красного апельсина, выразительными оттенками малинового джема, кофе мокко и цветов. Послевкусие длительное, сочное.', 'Вино прекрасно сочетается с мясными блюдами, особенно со стейком Рибай, и зрелыми сырами.', '', 'Сыр, Дичь, Говядина', 'Вино', '2024-02-03 11:37:26', '2024-02-03 11:37:26', NULL),
	('Красное', 'сухое', '13%', 'https://images.winechef.com/media/images/x454/5c05da0a768be/8cc2bbdfe6d5f28eb5cce90f620ebcf9.jpeg', 'Margaux AOC Premier Grand Cru Classe', '964c693d3d22', 'Шато Марго', '2021', 'Франция', 'Бордо', 'Chateau Margaux', 'Каберне Совиньон, Пти Вердо, Каберне Фран', 60000, 'Полнотелое', 'Аромат вина — роскошная симфония, сотканная из нот сочных черных и красных фруктов, лаврового листа, перца и кедра. Через время в букете проявляются оттенки сливы, малины и мяты.', 'Вино покоряет многогранным, полным, щедрым вкусом с бархатистой текстурой, нотами малины, сливы, чая Ройбуш, минералов и сандалового дерева, с утонченными танинами и долгим, соблазнительным послевкусием.', 'Вино идеально для использования в качестве аперитива. Также, оно прекрасно подойдет к запеченному на гриле мясу, твердым выдержанным сырам.', 'Фруктовые и ароматные, Элегантные и бархатистые', 'Сыр, Дичь, Говядина, Фуа-гра, Мясные закуски, Ягнятина, Аперитив, Свинина', 'Вино', '2024-02-03 11:37:26', '2024-02-03 11:37:26', NULL),
	('Красное', 'сухое', '13.5%', 'https://images.winechef.com/media/images/x454/5c90d28e776d8/3a1483194b317c1671d856ddf3b956ca.jpeg', 'Chateau Nenin Pomerol AOC', '7822da321e9d', 'Шато Ненан (Помроль) АОС', '2019', 'Франция', 'Бордо', 'Chateau Nenin', 'Мерло, Каберне Фран', 8467, 'Среднее', 'Вино обладает ароматом со сладкими нотками вишни и вишневого ликера, намеками цветов и минералов.', 'Вино имеет богатый, хорошо структурированный вкус с примечаниями лакричника, трюфелей, с тонкими лесными нотками.', 'Вино отлично сочетается с мясными блюдами (говядиной, бараниной под соусом), а также трюфелями, сырами и белыми грибами.', 'Фруктовые и ароматные, Элегантные и бархатистые', 'Сыр, Дичь, Говядина, Грибы', 'Вино', '2024-02-03 11:37:26', '2024-02-03 11:37:26', NULL),
	('Красное', 'сухое', '13%', 'https://images.winechef.com/media/images/x454/5c0935e24e2c8/e0b1b9939b41b3cb00fa36285429f7c5.jpeg', 'Chateau Fayat, Pomerol AOC', '1cd9aa8b7bfe', 'Шато Файят', '2018', 'Франция', 'Бордо', 'Chateau Fayat', 'Мерло, Каберне Фран', 6367, 'Среднее', 'Вино обладает глубоким ароматом, в котором гармонично сплетаются нотки черных фруктов, древесины и перца.', 'Вино демонстрирует гладкий, фруктовый вкус с нотами сливы и ежевики, нюансами кирша, земли, дуба и специй, с приятными танинами и долгим послевкусием.', 'Вино прекрасно сочетается с трюфелями, стейком из красного мяса, дичью, рагу и выдержанными сырами. Им также приятно наслаждаться в кругу друзей после приема пищи.', 'Фруктовые и ароматные, Элегантные и бархатистые, Пряные и пикантные', 'Дичь, Говядина, Грибы', 'Вино', '2024-02-03 11:37:26', '2024-02-03 11:37:26', NULL),
	('Красное', 'сухое', '14.9%', 'https://images.winechef.com/media/images/x454/5c08d488aeef6/3f1bae1d724b8452f055f0fac99f3d20.jpeg', 'Clos Quebrada De Macul, \"Alba de Domus\" Cabernet Sauvignon', '6faeb985b7f3', '\"Альба де Домус\" Каберне Совиньон', '2018', 'Чили', 'Центральная Долина', 'Clos Quebrada de Macul', 'Каберне Совиньон, Пти Вердо, Мерло, Каберне Фран', 2067, 'Среднее', 'Выразительный, многогранный аромат вина наполнен тонами малины, засахаренной вишни, трав, диких ягод и ментола.', 'Вино чарует шелковистым, округлым вкусом с оттенками спелых красных фруктов, минералов и подлеска, мелкозернистыми танинами и элегантным послевкусием.', 'Вино прекрасно сочетается с блюдами из красного мяса, особенно с ягненком, говядиной, мясом дикого кабана и страуса. ', '', '', 'Вино', '2024-02-03 11:37:26', '2024-02-03 11:37:26', NULL),
        ('Красное', 'сухое', '14%', 'https://images.winechef.com/media/images/x454/5c0860a693979/b67b1efc0f1e9000cb3d178a5962f8c0.jpeg', 'Clos Quebrada De Macul, \"Anka Pargua\"', '24a548b42729', '\"Анка Паргуа\"', '2019', 'Чили', 'Центральная Долина', 'Clos Quebrada de Macul', 'Каберне Совиньон, Пти Вердо, Сира, Мерло, Каберне Фран, Карменере', 1600, 'Среднее', 'Сложный, обильный аромат вина наполняют ноты черной смородины, трав, листьев томата, оливкового масла, ванили и тостов.', 'Вкус вина хорошо сбалансирован, обладает аппетитной кислотностью и сочными тонами зрелых фруктов. Намеки земли, кожи и минералов приходят только в послевкусии, отличающемся средней длительностью и щедростью.', 'Вино станет прекрасным сопровождением для блюд из красного мяса, таких как баранина, тушеная в красном вине, или каре ягненка, запеченное с травами, пасты с красным соусом, выдержанных сыров', '', '', 'Вино', '2024-02-03 11:37:26', '2024-02-03 11:37:26', NULL),
     ('Красное', 'сухое', '14.5%', 'https://images.winechef.com/media/images/x454/5c792de5769d5/adc49a46f251e48f04adf7c25f60e670.jpeg', 'Garage Wine Co., Garnacha', '037c450c8cbc', 'Гараж Вайн Ко., Гарнача', '2017', 'Чили', 'Центральная Долина', 'Garage Wine Co.', 'Гренаш/Гарнача', 2600, 'Полнотелое', 'Изящный и тонкий вначале цветочный аромат вина становится все глубже по мере аэрации. В его палитре смешиваются тона древесной коры, красных и черных фруктов, солодки, черного перца и специй.', 'Экспрессивный, роскошный вкус вина с многочисленными оттенками спелых красных фруктов, ежевики, вишни, цветов фиалки и специй, которые подчеркиваются минеральностью, элегантными танинами и отличной кислотностью. Послевкусие чистое и свежее.', 'Вино подается к зрелым сырам, шашлыкам, ягнятине, телятине, барбекю и запеченному мясу.', '', '', 'Вино', '2024-02-03 11:37:26', '2024-02-03 11:37:26', NULL),
     ('Красное', 'сухое', '14.5%', 'https://images.winechef.com/media/images/x454/5c094c86120fc/7272cea8adb58651eb72f41bc12d07b6.jpeg', 'Clos Quebrada De Macul, \"Domus Aurea\" Cabernet Sauvignon', 'b65539026a1e', '\"Домус Аурея\" Каберне Совиньон', '2020', 'Чили', 'Центральная Долина', 'Clos Quebrada de Macul', 'Каберне Совиньон, Пти Вердо, Мерло, Каберне Фран', 4200, 'Полнотелое', 'Зрелый, сладковатый аромат вина окутывает оттенками вишни, сушеных ягод ежевики, черной смородины и сливового пирога, усиливающихся с помощью намеков земли, эвкалипта, графита, тимьяна и дымка.', 'Красивый, эффектный вкус вина наполнен мясистыми тонами фруктов и ягод, пряными оттенками земли и трав. Яркая кислотность связана с округлыми, зрелыми танинами, которые обеспечивают длительность привлекательному послевкусию.', 'Вино хорошо сочетается с жареным красным мясом, грибными блюдами, твердыми сырами. ', '', '', 'Вино', '2024-02-03 11:37:26', '2024-02-03 11:37:26', NULL),
     ('Красное', 'сухое', '14.5%', 'https://images.winechef.com/media/images/x454/5c06045e155a5/e109fe0cb7402dfba6d66f302fcb6ad7.jpeg', 'Garage Wine Co., Cabernet Sauvignon', 'd06fdb7636e3', 'Гараж Вайн Ко., Каберне Совиньон', '2017', 'Чили', '', 'Garage Wine Co.', 'Каберне Совиньон, Каберне Фран', 1933, 'Среднее', 'Яркий аромат вина прекрасно демонстрирует выразительность тонов вишни, ежевики, цветов, трав и азиатских специй.', 'Концентрированный, сложный вкус вина не потерял изящества и сочности своих фруктово-ягодных тонов. Дубовая древесина оставила свое влияние в нюансах специй и мягкости танинов.', 'Вино является прекрасным дополнением к блюдам из жареного мяса, в том числе баранины, дичи, а также к зрелым сырам.', '', '', 'Вино', '2024-02-03 11:37:26', '2024-02-03 11:37:26', NULL),
     ('Красное', 'сухое', '13.8%', 'https://images.winechef.com/media/images/x454/5c79619ba9022/20933d68100bea22857f426832ebc141.jpeg', 'Garage Wine Co., Cabernet Franc', '3c9d557c11b4', 'Гараж Вайн Ко., Каберне Фран', '2020', 'Чили', '', 'Garage Wine Co.', 'Каберне Фран', 1933, 'Среднее', 'В богатом, запоминающемся аромате вина прослеживаются нюансы красных ягод, цветов и сладких фруктов.', 'В изысканно-тонком вкусе вина раскрывается мягкая округлость танинов и свежесть полевых трав, продолжающаяся в длительном послевкусии.', 'Вино составит хорошую пару макаронам и другим гарнирам с мясным соусом, блюдам из красного мяса,  сырам.', '', '', 'Вино', '2024-02-03 11:37:26', '2024-02-03 11:37:26', NULL),
     ('Красное', 'сухое', '13%', 'https://images.winechef.com/media/images/x454/5c0674ff68fa6/d40c2b0c47462b5b70ec8e58e9a768cf.jpeg', 'Garage Wine Co., Carignan Garnacha Mataro', 'c1468cafea2c', 'Гараж Вайн Ко., Кариньян Гарнача Матаро', '2021', 'Чили', 'Центральная Долина', 'Garage Wine Co.', 'Кариньяно, Гарнача Тинторерра, Матаро', 1933, 'Среднее', 'Изящный и тонкий аромат вина немного сдержан вначале. После аэрации он становится глубже и полнее, раскрывая ноты черных фруктов, ягод, подлеска и колы.', 'Тонкая нить кислотности проходит насквозь фруктово-пряного букета во вкусе вина. Плотное, но не слишком агрессивное послевкусие завершается легкими намеками специй и лесных ягод.', 'Вино рекомендуется подавать к говядине, свинине, мясу на гриле, пряным блюдам и твердым сырам.', '', 'Сыр, Дичь, Говядина, Паста', 'Вино', '2024-02-03 11:37:26', '2024-02-03 11:37:26', NULL),
     ('Красное', 'сухое', '13%', 'https://images.winechef.com/media/images/x454/5c056958a9e39/7e5fc88a9f93a8696327cb1ab534f166.jpeg', 'Garage Wine Co., Pais, Secano Interior DO', '63299b901d5c', 'Гараж Вайн Ко., Паис', '2022', 'Чили', 'Центральная Долина', 'Garage Wine Co.', 'Паис', 1367, 'Среднее', 'Энергичный и экспрессивный аромат вина щедро наполнен оттенками малины, вишневого ликера, азиатских специй, минералов и цветов.', 'Сочный, гладкий вкус вина демонстрирует всю силу и чистоту тонов красных фруктов. Шелковистые танины ощущаются только как деликатные намеки, продлевающие бальзамическое послевкусие.', 'Вино подается для сопровождения мясных закусок, жареного мяса, колбасных изделий. ', '', '', 'Вино', '2024-02-03 11:37:26', '2024-02-03 11:37:26', NULL),
     ('Красное', 'сухое', '14%', 'https://images.winechef.com/media/images/x454/5c79608893d87/355bdc43fa60f3545f07601986f43148.jpeg', 'Volcanes, \"Parinacota\" Limited Edition', 'bec251905090', 'Вольканес, \"Паринакота\" Лимитед Эдишн', '2021', 'Чили', 'Центральная Долина', 'Bodega Volcanes', 'Сира, Кариньяно', 2633, 'Полнотелое', 'Ароматический букет вина раскрывается выразительными нотами красных и черных фруктов, таких как черника, малина и слива, дополненными нюансами ревеня, фиалки, солодки, черного чая, мускатного ореха, кедровой древесины и маслин.', 'Вкус вина обладает особым характером, хорошим уровнем кислотности, полным телом и отличной структурой со значительными танинами, которые придают вину свежесть, энергию и элегантность. Послевкусие продолжительное и приятное.', 'Вино составит превосходную пару блюдам из баранины, стейкам, тушеному мясу, дичи, мясу утки и зрелым, ароматным сырам.', '', '', 'Вино', '2024-02-03 11:37:26', '2024-02-03 11:37:26', NULL),
     ('Красное', 'полусухое', '14%', '', 'Garage Wine Perverso', '5736fa6659ae', 'Гараж Вайн Перверсо', '', 'Чили', 'Центральная Долина', 'Garage Wine Co.', 'Каберне Совиньон, Мерло, Каберне Фран, Кариньян', 1300, 'Среднее', 'На фоне насыщенного фруктового аромата можно почувствовать ноты кожи и лакрицы.', 'Вино свежее с мягкими танинами и приятным, слегка сладковатым послевкусием.', 'Попробуйте это вино с красным мясом на гриле – тибоун-стейк средней или слабой прожарки или сочетайте с традиционными эмпанадас с разнообразными начинками: сыром, овощами или говядиной.', 'Фруктовые и ароматные, Элегантные и бархатистые, Пряные и пикантные', 'Сыр, Говядина, Овощи', 'Вино', '2024-02-03 11:37:26', '2024-02-03 11:37:26', NULL),
     ('Красное', 'сухое', '14%', 'https://images.winechef.com/media/images/x454/5c060226683d7/87b8d771e7b5f803fe1a023e40475d71.jpeg', 'De Wetshof, \"Naissance\" Cabernet Sauvignon', '6b12b8fc9ecd', '\"Нэссанс\" Каберне Совиньон', '2019', 'ЮАР', 'Западный Кейп', 'De Wetshof', 'Каберне Совиньон', 1533, 'Среднее', 'Приятный, соблазнительный аромат вина соткан из оттенков темных ягод, шоколада и древесины. Свежие фруктовые нотки, динамично развиваясь, обогащают букет, придавая ему изысканность и утонченность.', 'У вина щедрый и глубокий вкус, в котором тона красных фруктов сочетаются с ягодными акцентами, а нотки шоколада и пряностей украшают это гармоничное звучание.', 'Вино станет прекрасным сопровождением для жареного мяса, в том числе мяса-гриль, стейка, каре ягненка с запеченными овощами, колбас и ветчины, сыров.', '', '', 'Вино', '2024-02-03 11:37:26', '2024-02-03 11:37:26', NULL),
     ('Красное', 'сухое', '14.5%', 'https://images.winechef.com/media/images/x454/5c0650f8c4893/aa162b8a8dc8fd026232538bc8955529.jpeg', 'Simonsig, \"Tiara\"', '267040283e1a', '\"Тиара\"', '2018', 'ЮАР', 'Западный Кейп', 'Simonsig', 'Каберне Совиньон, Пти Вердо, Мерло, Мальбек, Каберне Фран', 2133, 'Среднее', 'Вино демонстрирует выразительный аромат, сотканный из нот черники, крем де Кассис, листьев табака и пряностей.', 'Вино привлекает сочным, округлым, фруктовым вкусом с плотными, сбалансированными танинами и легкими пряными нюансами в стойком послевкусии.', 'Вино рекомендуется подавать к тушеной говядине, стейкам, мясу на гриле, ягнятине, выдержанным сырам.', 'Фруктовые и ароматные, Пряные и пикантные', 'Сыр, Говядина, Ягнятина', 'Вино', '2024-02-03 11:37:26', '2024-02-03 11:37:26', NULL),
     ('Красное', 'сухое', '15.5%', '', 'Mollydooker, \"The Boxer\" Shiraz', 'c5838e714a53', '\"Боксер\" Шираз', '', 'Австралия', 'Южная Австралия', 'Mollydooker', 'Шираз', 2400, 'Среднее', 'Взрывной, смелый аромат вина атакует сочными оттенками вишни, сливы и ежевики, которые сочетаются с намеками специй для выпечки, табака и чая Earl Grey.', 'Вкус вина покоряет мягкостью, сбалансированными танинами и насыщенной фруктовой композицией. Тона черных ягод и фруктов звучат в унисон с нотами солодки, ванили, кофе со сливками и черного перца.', 'Вино хорошо сочетается блюдами из дичи, жареным или приготовленным на гриле красным мясом, зрелыми сырами.', 'Фруктовые и ароматные, Элегантные и бархатистые, Пряные и пикантные', 'Сыр, Дичь, Говядина', 'Вино', '2024-02-03 11:37:26', '2024-02-03 11:37:26', NULL),
     ('Красное', 'сухое', '13.5%', '', 'Sigma Vinum, \"Murmelstier\" Blaufrankisch Exclusive, Mittelburgenland DAC', '520a001f3f7e', 'Сигма Винум, \"Мурмелштиер\" Блауфранкиш Эксклюзив', '', 'Австрия', 'Бургенланд', 'WineSunLimited', 'Блауфранкиш', 1800, 'Среднее', 'Выразительный аромат вина наполнен оттенками темных ягод и сухофруктов, нотками спелой вишни, приправленными щепоткой графита.', 'Вкус вина насыщенный, сбалансированный, с интенсивными тонами спелой вишни, темных ягод и дуба, кремовой текстурой, хорошо интегрированными танинами и долгим, благородным послевкусием.', 'Вино прекрасно подойдет к блюдам из жареного или тушеного красного мяса, дичи, оленине, бефстроганов и мясным блюдам под соусом карри, а также сырам.', 'Фруктовые и ароматные, Элегантные и бархатистые, Пряные и пикантные', 'Сыр, Дичь, Говядина', 'Вино', '2024-02-03 11:37:26', '2024-02-03 11:37:26', NULL),
     ('Красное', 'сухое', '13%', '', 'Kloster Eberbach Pinot Noir trocken Crescentia', '343c84b0df45', 'Клостер Эбербах Пино Нуар трокен Кресцентия', '', 'Германия', 'Рейнгау', 'Kloster Eberbach', 'Шпетбургундер', 2067, 'Среднее', 'Щедрый аромат вина раскрывается оттенками вишни, ежевики, сливы, пряностей, черного перца и подлеска.', 'Вкус вина шелковистый, мягкий, с фруктово-древесными оттенками и округлыми танинами в долгом послевкусии.', 'Вино прекрасно сочетается с жареным мясом, мясом и рыбой, приготовленными на гриле. Составит гармоничную пару с выдержанными сырами.', 'Фруктовые и ароматные, Элегантные и бархатистые, Пряные и пикантные', 'Рыба, Сыр, Говядина', 'Вино', '2024-02-03 11:37:26', '2024-02-03 11:37:26', NULL),
